mapper.select:
SELECT i.*
FROM incoming i
         JOIN user u ON i.user_id = u.user_id
         JOIN admin a ON u.admin_id = a.admin_id
WHERE u.admin_id IN (
    SELECT admin_id FROM admin
    WHERE warehouse_id = (
        SELECT warehouse_id FROM admin WHERE admin_id = #{adminId}
    )
      AND role = '창고관리자'
)
  AND i.status = #{status}
